pub async fn test(client: Arc<Provider<Http>>, my_address: Address, erc20_address: Address) -> Result<()> {

    let mut multicall = Multicall::<Provider<Http>>::new(client.clone(),None).await?;
    let erc20_abi: Abi = serde_json::from_str(ABI)?;

    let contract: ContractInstance<Arc<Provider<Http>>, Provider<Http>> = Contract::<Provider<Http>>::new(erc20_address,erc20_abi,client.clone());

    multicall.add_call(contract.method::<_, String>("symbol",())?, false)
        .add_call(contract.method::<_, u8>("decimals", ())?, false)
        .add_call(contract.method::<_, U256>("balanceOf", my_address)?, false)
        .add_get_eth_balance(my_address, false);



    let response: (String, u8, U256, U256) = multicall.call().await?;

    let usdt_balance = format_units(response.2, response.1 as usize)?;
    let eth_balance = format_units(response.3, "ether")?;

    println!("My {}: {}", response.0, usdt_balance);
    println!("My Matic: {eth_balance}");

    Ok(())
}